mapper.select:
select a.id id,a.name name,u.name as owner,a.startDate startDate,a.endDate endDate
from tbl_activity a
join tbl_user u
on a.owner=u.id


        a.name like '%' #{name} '%'


        and u.name like '%' #{owner} '%'


        and a.startDate > #{startTime}


        and a.endDate < #{endTime}


order by a.createTime desc
limit #{skipCount},#{pageSize}

select count(*) from tbl_activity a join tbl_user u on a.owner=u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startTime}
and a.endDate < #{endTime}
